shader:
precision mediump float;

uniform float u_time;
uniform vec2 u_resolution;

vec3 palette(float t)
{
    vec3 a = vec3(1.088, 0.928, 0.928);
    vec3 b = vec3(0.478, 0.848, 0.638);
    vec3 c = vec3(0.903, 0.668, 0.608);
    vec3 d = vec3(1.067, 0.797, 0.528);

    return a + b * cos(6.283185 * (c * t + d));
}

void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - u_resolution.xy) / u_resolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(int i = 0; i < 3; i++) {
        float fi = float(i);
        vec3 col = vec3(0.0);

        if(mod(fi, 2.0) == 0.0) col = palette(length(uv0) + fi * 0.4 + u_time / 2.0);
        else col = palette(length(uv0) + fi * 0.4 - u_time / 2.0);

        uv = fract(uv * 1.7) - 0.5;
        float dist = length(uv);
        float frequency = 10.0;

        if(mod(fi, 2.0) == 0.0) dist = sin(dist * frequency + u_time) / frequency;
        else dist = sin(dist * frequency - u_time) / frequency;

        dist = abs(dist);
        dist = 0.005 / dist;

        finalColor += col * dist;
    }

    gl_FragColor = vec4(finalColor, 1.0);
}
